import React, { useState } from "react"
import { Link } from "react-router-dom"
import NavbarItems from '../data/Links.json'
import Modal from "../Modal/Modal"
import logo from '../images/logo3.png'
import "../style.css"
import "./Navbar.css"


export default function Navbar({ size, toggleCart }) {


    const [openModal, setModal] = useState(false)
    const [navbarIcon, setNavbarIcon] = React.useState("fas fa-bars") //Initialize menu icon
    const [menuOpen, setMenuOpen] = React.useState(false) //Init state -> closed

    function handleNavbar() {
        setNavbarIcon(prevIcon => prevIcon === "fas fa-bars" ? "fas fa-times" : "fas fa-bars");
        setMenuOpen(prevOpen => !prevOpen) // toggle the menu open/closed
    }

    return (
        <nav className="Navbar">
            <div className="NavbarItems">
                <div className="NavbarSiteBrand"><img src={logo} alt="logoImg" /></div>
                <div className="menu-icon"><i className={navbarIcon} onClick={handleNavbar}></i></div>
                <div className="NavbarLinks">
                    <ul className={`nav-menu ${menuOpen ? "active" : ""}`}>
                        {NavbarItems.map((item, index) => {
                            return (
                                // <li key={index} className={`${item.cName}`}>
                                //     <Link to={item.url} className="linkItem" onClick={() => { handleNavbar(); }} >{item.title}</Link>
                                // </li>
                                <li key={index} className={`${item.cName}`}>
                                    {item.url === '/#about' ?
                                        <Link to={`${item.url}`} className="linkItem" onClick={(e) => {
                                            e.preventDefault(); 
                                            console.log("about");
                                                                           // e.preventDefault();
                                            // Custom behavior for the "about" page
                                        }}>{item.title}</Link> :
                                        <Link to={item.url} className="linkItem" onClick={() => { handleNavbar(); }}>{item.title}</Link>
                                    }
                                </li>

                            )
                        })}
                        <li><a href="#about">test</a></li>
                        <button className="signUpBtn">Sing up</button>
                    </ul>
                </div>
                <div className="NavbarActions">
                    <div className="singUpIconSection">
                        <button onClick={() => setModal(true)}><i className="fa-solid fa-user"></i></button>
                    </div>
                    <Modal open={openModal} onClose={() => setModal(false)} />
                    <div className="cartIconSection">
                        <button onClick={toggleCart}><i className="fa-solid fa-cart-shopping"><span className="cartLength">
                            <small>( {size} )</small>
                        </span>
                        </i>
                        </button>
                    </div>
                </div>
            </div>
        </nav>
    )

}
