import Navbar from './components/Navbar/Navbar';
import Footer from './components/Footer/Footer';
import { Route, Routes, useLocation } from "react-router-dom"
import Home from './Routes/Home';
import Products from './Routes/Products';
import About from './Routes/About';
import Cart from './components/Cart/Cart'
import React, { useEffect } from 'react'
import Contact from './Routes/Contact';
import { QueryClient, QueryClientProvider } from 'react-query';
import { ReactQueryDevtools } from 'react-query/devtools';
import useCart from './CustomHooks/useCart';
import AppContext from './AppContext';


const queryClient = new QueryClient();

function App() {

  const { handleAddToCart, handleQuantityChange,
    toggleCart, setCartLength, setCart, cart,
    cartLength, cartVisible, setCartVisible } = useCart();

  // !scroll up
  const location = useLocation();
  const { pathname } = useLocation();
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [pathname]);



  return (
    <div className="App">
      <QueryClientProvider client={queryClient}>
        <AppContext.Provider value={{ handleAddToCart }}>
          <Navbar cartLength={cartLength} toggleCart={toggleCart} />
          <Routes location={location} key={location.path}>
            <Route path="/" element={<Home/>} />
            <Route path="/products" element={<Products/>} />
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
          </Routes>
          {/* {cartVisible && <Cart cart={cart} handleQuantityChange={handleQuantityChange} toggleCart={toggleCart} cartVisible={cartVisible} setCart={setCart} setCartVisible={setCartVisible} setCartLength={setCartLength} />} */}
          {cartVisible && <Cart cartVisible={cartVisible} setCartVisible  />}
          <Footer />
          <ReactQueryDevtools />
        </AppContext.Provider>
      </QueryClientProvider>
    </div>
  );
}

export default App;
