import React, { useState, useEffect } from "react"
import "./FeaturedCollectionSection.css"
import ProductCard from "../Product/ProductCard";
import { AnimatePresence,motion } from 'framer-motion'

const productLimit = 4;
const API_URL = 'https://fakestoreapi.com/';


export default function FeaturedCollections({handleAddToCart}) {


    const {ref,inView}=useInView();
    const animation=useAnimation();

    useEffect

    const [products, setProducts] = useState([]); //init it with empty array
    const [category, setCategory] = useState('all');

    useEffect(() => {
        async function fetchProducts() {
            let url = `${API_URL}products?limit=${productLimit}`;

            if (category !== 'all') {
                url = `${API_URL}products/category/${category}?limit=${productLimit}`;
            }
            try {
                const response = await fetch(url);
                const data = await response.json();
                const filteredData = data.filter((product) => product.category !== 'electronics');

                if (category !== 'all') {
                    setProducts([]);
                }

                await setProducts(filteredData);
                console.log();
            } catch (error) {
                console.error("Failed to fetch data. Please try again later.");
            }
        }
        fetchProducts();

    }, [category])

    return (
        <motion.section className="featuredCollections">
            <div className="featuredCollectionsContainer">
                <div className="featuredCollections--Header">
                    <h2>Featured Collections</h2>
                </div>
                <div className="featuredCollections--List">
                    <ul>
                        {/* <li>
                            <button onClick={() => setCategory("all")}>ALL</button>
                        </li> */}
                        <li>
                            <button onClick={() => setCategory("men's clothing")}>Mens</button>
                        </li>
                        <li>
                            <button onClick={() => setCategory("women's clothing")}>Woman</button>
                        </li>
                        <li>
                            <button onClick={() => setCategory("jewelery")}>Jewelery</button>
                        </li>
                    </ul>
                </div>
                <div className="featured--productCardList">
                    {products.map((product) => (
                        <ProductCard handleAddToCart={handleAddToCart} product={product} key={product.id}/>
                    ))}
                </div>
            </div>
        </motion.section >
    )
}
