import { useState, useEffect } from 'react';
const useCart = () => {
    const [cart, setCart] = useState([]);
    const [cartVisible, setCartVisible] = useState(false);



    useEffect(() => { //!init
      const initialCart = JSON.parse(localStorage.getItem('cart')) || [];
      setCart(initialCart);
    }, []);





    useEffect(() => { //!setItem
      localStorage.setItem('cart', JSON.stringify(cart));
    }, [cart]);

    const addToCart = (item) => { //!add to cart
      const existingItem = cart.find((cartItem) => cartItem.id === item.id);

      if (existingItem) {
        const updatedCart = cart.map((cartItem) =>
          cartItem.id === item.id ? { ...cartItem, quantity: cartItem.quantity + 1 } : cartItem
        );
        setCart(updatedCart);
      } else {
        const updatedCart = [...cart, { ...item, quantity: 1 }];
        setCart(updatedCart);
      }
    };

    const removeFromCart = (itemId) => {
      const updatedCart = cart.filter((cartItem) => cartItem.id !== itemId);
      setCart(updatedCart);
    };

    const clearCart = () => {
      setCart([]);
    };

    const handleAddToCart = (item) => {
          const existingItem = cart.find((cartItem) => cartItem.id === item.id);

          if (existingItem) {
            setCart(cart.map((cartItem) => (cartItem.id === item.id ? { ...cartItem, quantity: cartItem.quantity + 1 } : cartItem)));
          } else {
            setCart([...cart, { ...item, quantity: 1 }]);
          }
        };

    const setCartLength = () => {
      // Calculate the cart length based on the updated cart state
    };

    const cartLength = cart.reduce((total, item) => total + item.quantity, 0);

    return {handleAddToCart, setCart, cartLength, addToCart, removeFromCart, clearCart, cartVisible, setCartVisible, setCart, setCartLength };
  };

  export default useCart;
