import { motion } from 'framer-motion';
import Sidebar from '../SideBar/Sidebar';
import ProductCard from './ProductCard';
import { useState, useEffect } from 'react';
import './ProductList.css';
import useFetch from '../../CustomHooks/useFetch';

const productLimit = 4;

export default function ProductList({ toggleSidebar, showSidebar }) {
  const [products, setProducts] = useState([]);
  const [selectedCategories, setSelectedCategories] = useState([]);

  const handleCategoryChange = (selectedCategories) => {
    setSelectedCategories(selectedCategories);
  };

  const { data: fetchedProducts, loading: isLoading } = useFetch(
    'https://fakestoreapi.com/products'
  );

  useEffect(() => {
    if (fetchedProducts) {
      let filteredData = fetchedProducts;
      if (selectedCategories.length > 0) {
        filteredData = fetchedProducts.filter((product) =>
          selectedCategories.includes(product.category)
        );
      }
      const limitedData = filteredData.slice(0, productLimit);
      setProducts(filteredData);
    }
  }, [fetchedProducts, selectedCategories]);

  if (isLoading) {
    return <p>Loading...</p>;
  }

  return (
    <div className="productListContainer">
      <Sidebar
        onCategoryChange={handleCategoryChange}
        toggleSidebar={toggleSidebar}
        showSidebar={showSidebar}
      />
      <div className="productCardList">
        <div className="productListHeader">
          <h2>Products</h2>
          <button className="filterButton" onClick={toggleSidebar}>
            Filter
          </button>
        </div>
        <motion.div layout className="productListContent">
          {products &&
            products.map((product) => (
              <ProductCard product={product} key={product.id} />
            ))}
        </motion.div>
      </div>
    </div>
  );
}
