import "./Sidebar.css"
import { useState, useEffect } from 'react';
import categories from "./sidebarCategories.json"

export default function Sidebar(props) { 

    const { onCategoryChange, toggleSidebar, showSidebar } = props;//destructuring assignment  
    const [selectedCategories, setSelectedCategories] = useState([]); //to pass categories if url exists.


    useEffect(() => { 
        const queryParams = new URLSearchParams(window.location.search);
        const categoryParam = queryParams.get('category');
        if (categoryParam) { //when i have parameters.

            // use the parameters.
            setSelectedCategories([categoryParam]);
            onCategoryChange([categoryParam]); //to pass the categoryChange back to productList

        }
    }, []);


    const handleCheckboxChange = (event) => {

        const checkboxValue = event.target.value;
        let newSelectedCategories = []; //to change the selectedCategories i want this variable.

        if (selectedCategories.includes(checkboxValue)) { 
            // If the checkbox value is already in the selected categories, remove it
            newSelectedCategories = selectedCategories.filter((category) => category !== checkboxValue);
        } else {
            // Otherwise, add the checkbox value to the selected categories
            newSelectedCategories = [...selectedCategories, checkboxValue];
        }



        // Update the selected categories and call the callback function
        setSelectedCategories(newSelectedCategories);
        onCategoryChange(newSelectedCategories);

        // Construct the new URL with the modified parameters
        const queryParams = new URLSearchParams(window.location.search);
        queryParams.set("category", newSelectedCategories.join(","));
        const newUrl = `${window.location.pathname}?${queryParams.toString()}`;

        // Update the URL
        window.history.pushState(null, '', newUrl);
    };

    return (
        <div className={`sidebarContainer ${showSidebar ? 'active' : ''}`}>
            <div className="sidebarContent">

                <h2>Categories</h2>
                <ul>
                    {categories.map((category) => (
                        <li className="categoryItem" key={category.id}>

                            <input type="checkbox" value={category.name}
                                checked={selectedCategories.includes(category.name)} //It's set to true if the selectedCategories array includes the category name and false otherwise.
                                onChange={handleCheckboxChange}
                            />
                            <p>{category.label}</p>
                        </li>
                    ))}
                    <button className="saveBtn" onClick={toggleSidebar}>Save & Search</button>

                </ul>
            </div>

        </div>
    )



}
