import Navbar from './components/Navbar/Navbar';
import Footer from './components/Footer/Footer';
import { Route, Routes, useLocation } from "react-router-dom"
import Home from './Routes/Home';
import Products from './Routes/Products';
import About from './Routes/About';
import Cart from './components/Cart/Cart'
import React, { useState, useEffect } from 'react'
import Contact from './Routes/Contact';
import { QueryClient, QueryClientProvider } from 'react-query';
import { ReactQueryDevtools } from 'react-query/devtools';

// Create a QueryClient instance
const queryClient = new QueryClient();

function App() {
  const location = useLocation();
  const { pathname } = useLocation();
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [pathname]);

  // // Retrieve cart data from local storage or initialize an empty array
  // const initialCart = JSON.parse(localStorage.getItem('cart')) || [];
  // const [cart, setCart] = useState(initialCart);
  const [cartLength, setCartLength] = useState(0);


  const handleAddToCart = (item) => {
    const existingItem = cart.find((cartItem) => cartItem.id === item.id);

    if (existingItem) {
      setCart((prevCart) =>
        prevCart.map((cartItem) =>
          cartItem.id === item.id
            ? { ...cartItem, quantity: cartItem.quantity + 1 }
            : cartItem
        )
      );
    } else {
      setCart((prevCart) => [...prevCart, { ...item, quantity: 1 }]);
    }
  };

  // Update local storage and cart length whenever the cart changes
  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
    setCartLength(
      cart.reduce((total, item) => total + item.quantity, 0)
    );
  }, [cart]);



  // // Update local storage whenever the cart changes
  // useEffect(() => {
  //   localStorage.setItem('cart', JSON.stringify(cart));
  //   setCartLength(cart.reduce((total, item) => total + item.quantity, 0));
  // }, [cart]);

  // const handleAddToCart = (item) => {
  //   const existingItem = cart.find((cartItem) => cartItem.id === item.id);

  //   if (existingItem) {
  //     setCart(cart.map((cartItem) => (cartItem.id === item.id ? { ...cartItem, quantity: cartItem.quantity + 1 } : cartItem)));
  //   } else {
  //     setCart([...cart, { ...item, quantity: 1 }]);
  //   }
  // };

  const [cartVisible, setCartVisible] = useState(false);
  const toggleCart = () => {
    setCartVisible(!cartVisible);
  };

  return (
    <div className="App">
      <QueryClientProvider client={queryClient}>
        <Navbar cartLength={cartLength} toggleCart={toggleCart} />
        <Routes location={location} key={location.path}>
          <Route path="/" element={<Home />} />
          {/* <Route path="/products" element={<Products handleAddToCart={handleAddToCart} />} /> */}
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
        </Routes>
        {/* {cartVisible && <Cart cart={cart} toggleCart={toggleCart} cartVisible={cartVisible} setCart={setCart} setCartLength={setCartLength} />} */}
        {cartVisible && <Cart toggleCart={toggleCart} cartVisible={cartVisible}  />}
        <Footer />
        <ReactQueryDevtools />
      </QueryClientProvider>
    </div>
  );
}

export default App;
