import "./Sidebar.css"
import { useState, useEffect } from 'react';
import categories from "./sidebarCategories.json"

export default function Sidebar(props,toggleSidebar) { //props is a callback function. - The data goes back to parent and then pass it into other component.



    // toggleSidebar={toggleSidebar}


    // const [showSidebar, setShowSidebar] = useState(false);

    // const toggleSidebar=()=>{
    //     setShowSidebar(!showSidebar);
    // }





    const { onCategoryChange } = props; //destructuring assignment - extracting the onCategoryChange function
    const [selectedCategories, setSelectedCategories] = useState([]);



    // check for parameters.If exists,then use them.
    useEffect(() => {
        const queryParams = new URLSearchParams(window.location.search);
        const categoryParam = queryParams.get('category');
        if (categoryParam) {

            // use the parameters.
            setSelectedCategories([categoryParam]);
            onCategoryChange([categoryParam]);

            // THEN remove them...I dont want the user be able to check them.
            // Delete the "category" parameter
            queryParams.delete("category");

            // Construct the new URL with the modified parameters
            const newUrl = `${window.location.pathname}?${queryParams.toString()}`;

            // Update the URL
            window.history.pushState(null, '', newUrl);
        }
        else {
            //display them all
            const allCategories = categories.map((category) => category.name);
            setSelectedCategories(allCategories);
            onCategoryChange(allCategories);
        }
    }, []);

    // Every time you choose a category then display the products of this category.
    const handleCheckboxChange = (event) => {
        const checkboxValue = event.target.value;
        let newSelectedCategories = [];

        if (selectedCategories.includes(checkboxValue)) {
            // If the checkbox value is already in the selected categories, remove it
            newSelectedCategories = selectedCategories.filter(
                (category) => category !== checkboxValue
            );
        } else {
            // Otherwise, add the checkbox value to the selected categories
            newSelectedCategories = [...selectedCategories, checkboxValue];
        }
        // Update the selected categories and call the callback function
        setSelectedCategories(newSelectedCategories);
        onCategoryChange(newSelectedCategories);
    };
    return (
        <div className={'sidebarContainer ${'}>
            <div className="sidebarContent">

                <h2>Categories</h2>
                <ul>
                    {categories.map((category) => (
                        <li className="categoryItem" key={category.id}>

                            <input type="checkbox" value={category.name}
                                checked={selectedCategories.includes(category.name)} //determine whether the checkbox should be checked or unchecked. - this if returns true or undefined(default state)
                                onChange={handleCheckboxChange}
                            />
                            <p>{category.label}</p>
                        </li>
                    ))}
                </ul>
            </div>

        </div>
    )



}
