    // display sidebar
    const [showSidebar, setShowSidebar] = useState(false);

    const toggleSidebar = () => {
        setShowSidebar(!showSidebar);
        console.log(showSidebar)
    }

    return (
        <div className="productPageContainer">
            <ProductList handleAddToCart={handleAddToCart} toggleSidebar={toggleSidebar} showSidebar={showSidebar}/>
        </div>
    );
}
