import './categoriesSection.css'
import menFashion3 from '../images/men_fashion3.jpg'
import womanFashion from '../images/womanFashion3.jpg'
import jewellery from '../images/jewellery2.jpg'
import React,{useState} from 'react';
import Sidebar from './Sidebar';
import { Link } from 'react-router-dom'

export default function CategorySection() {
    const [selectedCategory, setSelectedCategory] = useState('');

    const handleCategoryClick = (category) => {
      setSelectedCategory(category);
    };

    return (
      <section className="categorySection">
        <h2>Categories</h2>
        <div className="categoryContainer">
          <div className='categoryCard'>
            <Link to={{ pathname: '/products', search: `?category=men'` }}>
              <div className='categoryCardImage'>
                <img src={menFashion3} />
              </div>
              <div className='categoryTitle' onClick={() => handleCategoryClick("men's clothing")}>Men Fashion</div>
            </Link>
          </div>
        </div>
      </section>
    );
  }
