import React, { useState } from "react"
import "./style.css"
import "./Navbar.css"
import { NavbarItems } from "./NavbarItems"

export default function Navbar() {


    const [navbarIcon, setNavbarIcon] = React.useState("fas fa-bars") //Initialize menu icon


    function handleNavbar(){
        setNavbarIcon(prevIcon => prevIcon === "fas fa-bars" ? "fas fa-times" : "fas fa-bars");
    }

    return (
        <nav className="Navbar">
            <div className="NavbarItems">
                <div className="NavbarSiteBrand">Ecommerce</div>
                <div className="menu-icon">
                    <i className={navbarIcon} onClick={handleNavbar}></i>
                    {/* <i className={navbarIcon}></i> */}
                </div>
                <div className="NavbarLinks">
                    <ul className="nav-menu">
                        {NavbarItems.map((item, index) => {
                            return (
                                <li key={index}
                                    className={item.cName}>
                                    {/* <i className={item.icon}></i>{item.title}     */}
                                    {item.title}
                                </li>
                            )
                        })}

                    </ul>
                </div>
                <div className="NavbarActions">
                    <i className="fa-solid fa-user"></i>
                    <i class="fa-solid fa-cart-shopping"></i>
                </div>
            </div>


        </nav>

    )

}
