import { useInView } from 'react-intersection-observer';
import { motion } from 'framer-motion';

export default function Home({ handleAddToCart }) {
  const [ref, inView] = useInView({
    threshold: 0.5,
    triggerOnce: true,
  });

  const handleIntersection = () => {
    console.log('Component is now visible!');
  };

  const fadeInAnimation = {
    opacity: inView ? 1 : 0,
    transition: { duration: 0.6, ease: "easeInOut" },
  };

  return (
    <motion.div>
      <motion.div ref={ref} initial={{ opacity: 0 }} animate={fadeInAnimation}>
        <Banner />
      </motion.div>
      <motion.div animate={fadeInAnimation}>
        {inView && <Info />}
      </motion.div>
      <motion.div ref={ref} initial={{ opacity: 0 }} animate={fadeInAnimation}>
        <FeaturedCollections handleAddToCart={handleAddToCart} />
      </motion.div>
      <motion.div animate={fadeInAnimation}>
        {inView && <CategorySection />}
      </motion.div>
      <motion.div ref={ref} initial={{ opacity: 0 }} animate={fadeInAnimation}>
        <AboutUs />
      </motion.div>
    </motion.div>
  );
}
