import Navbar from './components/Navbar/Navbar';
import Footer from './components/Footer/Footer';
import { Route, Routes } from "react-router-dom"
import Home from './Routes/Home';
import Products from './Routes/Products';
import Cart from './components/Cart/Cart'
import { useState,useEffect} from 'react'
function App() {
  useEffect(() => {
    // ðŸ‘‡ï¸ scroll to top on page load
    window.scrollTo({top: -10, left: 0, behavior: 'smooth'});
  }, []);

  // about cart.

  // 1. add to cart
  const [cart, setCart] = useState([]);


  const handleAddToCart = (item) => {

    const existingItemIndex = cart.findIndex((cartItem) => cartItem.id === item.id); //checks if productID already exists. - If yes then return the Index.

    if (existingItemIndex !== -1) { //if exists then..

      const updatedCart = [...cart]; //generate a copy of current cart array -This is done to ensure
      //that the original cart array is not modified directly.

      const updatedItem = { ...updatedCart[existingItemIndex] }; //This creates a copy of the existing item object that needs to be updated 
      //The existingItemIndex variable is used to access the existing 
      updatedItem.quantity += 1; //This updates the quantity property of the updatedItem object by adding 1 to it.

      updatedCart[existingItemIndex] = updatedItem; //This replaces the existing item object in the 
      //updatedCart array with the updated item object. 


      setCart(updatedCart);


      // Î•Î½ ÏƒÏ…Î½Ï„Î¿Î¼Î¯Î±: Î‘ÏÏ‡Î¹ÎºÎ± ÎµÎ»ÎµÎ³Ï‡Î¿Ï…Î¼Îµ ÎµÎ±Î½ Ï…Ï€Î±ÏÏ‡ÎµÎ¹ Ï„Î¿ Î±Î½Ï„Î¹ÎºÎµÎ¹Î¼ÎµÎ½Î¿ ÏƒÏ„Î¿ cart. Î•Î±Î½ Î½Î±Î¹ Ï„Î¿Ï„Îµ Î²ÏÎ¹ÏƒÎºÎ¿Ï…Î¼Îµ Ï„Î¿ INDEX Ï„Î¿Ï….
      // Î•Ï€ÎµÎ¹Ï„Î± Ï†Ï„Î¹Î±Ï‡Î½Î¿Ï…Î¼Îµ ÎµÎ½Î± Î±Î½Ï„Î¹Î³ÏÎ±Ï†Î¿ Ï„Î¿Ï… Cart Î³Ï„ Î´Î½ Î¼Ï€Î¿ÏÎ¿Ï…Î¼Îµ Î½Î± Ï€ÎµÎ¹ÏÎ¬Î¾Î¿Ï…Î¼Îµ ÎºÎ±Ï„ÎµÏ…Î¸ÎµÎ¯Î±Î½ Ï„Î¿ Î±ÏÏ‡Î¹ÎºÎ¿ object cart.
      // ÎœÎµÏ„Î± Ï€ÏÎµÏ€ÎµÎ¹ Î½Î± Î²ÏÎ¿ÏÎ¼Îµ Ï„Î¿ Î±Î½Ï„Î¹ÎºÎµÎ¹Î¼ÎµÎ½Î¿ Ï€Î¿Ï… Ï…Ï€Î¬ÏÏ‡ÎµÎ¹ Î·Î´Î· Î¼ÎµÏƒÎ± ÎºÎ±Î¹ Î½Î± Î±Ï…Î¾Î®ÏƒÎ¿Ï…Î¼Îµ Ï„Î·Î½ Ï€Î¿ÏƒÏŒÏ„Î·Ï„Î±...ÎžÎµÏÎ¿Ï…Î¼Îµ Ï„Î¿ index Ï„Î¿Ï…(existingItemIndex) ÎŸÎ ÎŸÎ¤Î• 
      // Ï€Î±Î¼Îµ ÏƒÏ„Î¿Î½ updatedCart Ï€Î¹Î½Î±ÎºÎ± ÎºÎ±Î¹ Ï€Î±Î¯ÏÎ½Î¿Ï…Î¼Îµ Î¼Î¿Î½Î¿ Î±Ï…Ï„Î¿ Ï„Î¿ Î±Î½Ï„Î¹ÎºÎµÎ¹Î¼ÎµÎ½Î¿ Ï€Î¿Ï… Î¼Î±Ï‚ ÎµÎ½Î´Î¹Î±Ï†Î­ÏÎµÎ¹ ÎºÎ±Î¹ Ï„Î¿Ï… Î±Ï…Î¾Î¬Î½Î¿Ï…Î¼Îµ Ï„Î·Î½ Ï€Î¿ÏƒÏŒÏ„Î·Ï„Î±.
      // Î¤ÎµÎ»Î¿Ï‚ Î±Ï†Î¿Ï… Ï„Î± ÎºÎ±Î½Î¿Ï…Î¼Îµ Î¿Î»Î± Î±Ï…Ï„Î± Î¼Ï€Î¿ÏÎ¿Ï…Î¼Îµ Î½Î± Ï„ÏÎ¿Ï€Î¿Ï€Î¿Î¹Î·ÏƒÎ¿Ï…Î¼Îµ Ï„Î¿ Î±ÏÏ‡Î¹ÎºÎ¿ Cart Î¼Îµ Ï„Î·Î½ ÏƒÏ…Î½Î±ÏÏ„Î·ÏƒÎ· setCart 

    } else {

      setCart([...cart, { ...item, quantity: 1 }]);

    }
  };





  // 2. Display Cart
  const [cartVisible, setCartVisible] = useState(false);
  const toggleCart = () => {
    setCartVisible(!cartVisible);
  }


  return (
    <div className="App">
      <Navbar size={cart.length} toggleCart={toggleCart} /> toggleCart is responsible for handleClickOnCart -- its set the cartVisible=true/false in every click
      <Routes>
        <Route path="/" element={<Home handleAddToCart={handleAddToCart} />} />
        <Route path="/products" element={<Products handleAddToCart={handleAddToCart} />} />
      </Routes>
      {cartVisible && <Cart cart={cart} cartVisible={cartVisible} setCart={setCart} />} {/*If cartVisible then render the component */}
      <Footer />

    </div>
  );
}

export default App;
