import React from "react"
import { NavbarItems } from "./NavbarItems"
import { Link } from "react-router-dom"
import "../style.css"
import "./Navbar.css"


export default function Navbar({ size }) {



    const [navbarIcon, setNavbarIcon] = React.useState("fas fa-bars") //Initialize menu icon
    const [menuOpen, setMenuOpen] = React.useState(false) //Init state -> closed


    function handleNavbar() {
        setNavbarIcon(prevIcon => prevIcon === "fas fa-bars" ? "fas fa-times" : "fas fa-bars");
        setMenuOpen(prevOpen => !prevOpen) // toggle the menu open/closed


    }

    return (
        <nav className="Navbar">
            <div className="NavbarItems">
                <div className="NavbarSiteBrand">Ecommerce</div>
                <div className="menu-icon">
                    <i className={navbarIcon} onClick={handleNavbar}></i>
                    {/* <i className={navbarIcon}></i> */}
                </div>
                <div className="NavbarLinks">
                    <ul className={`nav-menu ${menuOpen ? "active" : ""}`}>
                        {NavbarItems.map((item, index) => {
                            return (
                                <li key={index}
                                    className={item.cName}>
                                    {/* <i className={item.icon}></i>{item.title}     */}
                                    {/* <Link to={item.url}>{item.title}</Link> */}

                                    {/* <a href="/">{item.title}</a> */}
                                    <Link to={item.url} className="linkItem" onClick={handleNavbar}>{item.title}</Link>
                                </li>
                            )
                        })}
                        <button className="signUpBtn">Sing up</button>


                    </ul>
                </div>
                <div className="NavbarActions">
                    <div className="singUpBtn">
                        <button><i className="fa-solid fa-user"></i></button>
                    </div>
                    <div></div>
                    <button><i className="fa-solid fa-cart-shopping"><span className="cartLength">
                        <small>{size}</small>
                    </span>
                    </i>
                    </button>
                </div>
            </div>


        </nav>

    )

}
