import Banner from "../components/Banner/Banner";
import Info from "../components/InfoSection/info";
import FeaturedCollections from "../components/FeaturedCollectionSection/FeaturedCollectionSection"
import CategorySection from "../components/CategoriesSection/CategorySection";
import { motion } from 'framer-motion'

export default function Home() {
    const handleAddToCart = (item) => {
        // Get the existing cart data from localStorage
        const cartData = JSON.parse(localStorage.getItem('cart')) || [];

        // Check if the item already exists in the cart
        const existingItem = cartData.find((cartItem) => cartItem.id === item.id);

        if (existingItem) {
          // Increment the quantity of the existing item
          existingItem.quantity += 1;
        } else {
          // Add the new item to the cart
          cartData.push({ ...item, quantity: 1 });
        }

        // Update the cart data in localStorage
        localStorage.setItem('cart', JSON.stringify(cartData));
      };
    return (
        <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.2 }}
        exit={{ opacity: 0 }}>
            <Banner />
            <Info />
            <FeaturedCollections handleAddToCart={handleAddToCart} />
            <CategorySection />
        </motion.div>
    )


}
