import React, { useState } from "react"
import './Cart.css'
import productImage from '../images/tshirt.jpg'


export default function Cart({ cart, cartVisible, setCart }) {

    // const [price, setPrice] = useState(0);


    const handleQuantityChange = (item, change) => {

        const updatedCart = [...cart];
        const updatedItemIndex = updatedCart.findIndex((cartItem) => cartItem.id === item.id);
        const updatedItem = { ...updatedCart[updatedItemIndex] };
        updatedItem.quantity += change;

        if (updatedItem.quantity <= 0) {
            updatedCart.splice(updatedItemIndex, 1);
        } else {
            updatedCart[updatedItemIndex] = updatedItem;
        }

        setCart(updatedCart);

    }






    return (

        // <div className={`CartContainer ${cartVisible ? 'visible' : ''}`}>
        <div className="CartContent">

        <div className={`CartContainer ${cartVisible ? 'visible' : ''}`}>
            {console.log("cartVisible is :", cartVisible)}

            {
                cart.map((cartItem) => (
                    <div className="cardItem">
                        <div className="imageCartContainer">
                            <img src={cartItem.image} />
                        </div>
                        <div className="productCartInfo">
                            <h2 className="cartProductTitle">{cartItem.title}</h2>
                            <div className="quantityCartSection">
                                <p>
                                    Quantity:
                                    <button onClick={() => handleQuantityChange(cartItem, -1)}>-</button>
                                    <span>{cartItem.quantity}</span>
                                    <button onClick={() => handleQuantityChange(cartItem, 1)}>+</button>
                                </p>
                            </div>
                        </div>
                    </div>

                ))
            }
            <div className="totalCost">totalCost</div>

        </div >
        </div>
    )
}
