import React from "react";
import { motion } from 'framer-motion';
import { useContext } from 'react';
import CartContext from "../../Context/CartContext";
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';

const useStyles = makeStyles((theme) => ({
  productCard: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
    height: '100%',
  },
  productImg: {
    paddingTop: '56.25%', // 16:9 aspect ratio
    position: 'relative',
  },
  img: {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    objectFit: 'cover',
  },
  productInfo: {
    padding: theme.spacing(2),
  },
  productTitle: {
    fontSize: '1.2rem',
    fontWeight: 'bold',
    marginBottom: theme.spacing(1),
  },
  productPrice: {
    fontSize: '1rem',
    marginBottom: theme.spacing(2),
  },
  addToCart: {
    display: 'flex',
    justifyContent: 'center',
    marginBottom: theme.spacing(2),
  },
}));

export default function ProductCard({ product }) {
  const classes = useStyles();
  const { handleAddToCart } = useContext(CartContext);

  return (
    <motion.div layout>
      <Card className={classes.productCard} key={product.id}>
        <div className={classes.productImg}>
          <CardMedia
            component="img"
            alt="Product Image"
            image={product.image}
            className={classes.img}
          />
        </div>
        <CardContent className={classes.productInfo}>
          <Typography variant="h6" component="div" className={classes.productTitle}>
            {product.title}
          </Typography>
          <Typography variant="body1" color="textSecondary" className={classes.productPrice}>
            {product.price}$
          </Typography>
        </CardContent>
        <div className={classes.addToCart}>
          <Button variant="contained" color="primary" onClick={() => handleAddToCart(product)}>
            Add to Cart
          </Button>
        </div>
      </Card>
    </motion.div>
  );
}
