import './categoriesSection.css'

import React from 'react';
import { Link } from 'react-router-dom'
import categoryData from "../data/categories.json"
import { useContext, useState } from 'react';

import { MyContext } from '../MyContext.js';


export default function CategorySection() {

    const { selectedCategory, setSelectedCategory } = useContext(MyContext);


    const handleCategoryClick = (categoryName) => {
        console.log("handleCat:")
        setSelectedCategory(categoryName);
    };


    return (
        <section className="categorySection">
            <h2>Categories</h2>

            <div className="categoryContainer">
                {categoryData.categories.map(category => (
                    <div className='categoryCard' key={category.categoryID}>
                        {/* <Link to={category.categoryUrl}> */}
                        <Link to={category.categoryUrl} onClick={() => handleCategoryClick(category.CategoryName)}>
                            <div className='categoryCardImage'>
                                <img src={category.categoryImage} alt='' />
                            </div>
                            <div className='categoryTitle'>{category.CategoryTitle}</div>
                        </Link>
                    </div>
                ))}
            </div>

        </section>
    )


}
