import "./Sidebar.css";
import { useState, useEffect } from "react";
import categories from "./sidebarCategories.json";

export default function Sidebar(props) {
    const { onCategoryChange, selectedCategories: initialSelectedCategories } =
        props;

    const [selectedCategories, setSelectedCategories] = useState(
        initialSelectedCategories || []
    );

    const handleCheckboxChange = (event) => {
        const checkboxValue = event.target.value;
        let newSelectedCategories = [];

        if (selectedCategories.includes(checkboxValue)) {
            // If the checkbox value is already in the selected categories, remove it
            newSelectedCategories = selectedCategories.filter(
                (category) => category !== checkboxValue
            );
        } else {
            // Otherwise, add the checkbox value to the selected categories
            newSelectedCategories = [...selectedCategories, checkboxValue];
        }

        // Update the selected categories and call the callback function
        setSelectedCategories(newSelectedCategories);
        onCategoryChange(newSelectedCategories);
    };

    useEffect(() => {
        setSelectedCategories(initialSelectedCategories || []);
    }, [initialSelectedCategories]);

    return (
        <div className="sidebarContainer">
            <div className="sidebarContent">
                <h2>Categories</h2>
                <ul>
                    {categories.map((category) => (
                        <li className="categoryItem" key={category.id}>
                            type="checkbox"
                            value={category.name}
                            checked={selectedCategories.includes(category.name)}
                            onChange={handleCheckboxChange}
/>
                            <p>{category.label}</p>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}
