import React, { useState,useEffect } from "react"
import './Cart.css'
import productImage from '../images/tshirt.jpg'


export default function Cart({ cart, cartVisible,setCart }) {

    const [price, setPrice] = useState(0);

    const [isVisible, setIsVisible] = useState(true);

    useEffect(() => {
        setIsVisible(cartVisible);
      }, [cartVisible]);

    return (

        <div className={`CartContainer ${isVisible ? 'visible' : ''}`}>
        {
                cart.map((cartItem) => (
                    <div className="cardItem">
                        <div className="imageCartContainer">
                            <img src={cartItem.image} />
                        </div>
                        <div className="productCartInfo">
                            <h2>{cartItem.title}</h2>
                            <p>Quantity:1</p>
                        </div>
                    </div>

                ))

            }

        </div >
    )
}
