import { React, useState } from 'react';
import './Modal.css';

export default function Modal({ open, onClose }) {
  let [authMode, setAuthMode] = useState('Sign In');
  let [isLoggedIn, setIsLoggedIn] = useState(false); // add new state variable

  const changeAuthMode = () => {
    setAuthMode(authMode === 'Sign In' ? 'Sign Up' : 'Sign In');
  };

  const handleLogin = (event) => {
    event.preventDefault();
    setIsLoggedIn(true);
    onClose();
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
  };

  if (!open) return null;

  return (
    <div className='Modal'>
      <div className='ModalContent'>
        <div className='signInHeader'>
          <h2>{isLoggedIn ? 'Logged In' : authMode}</h2> {/* update header to show whether the user is logged in or not */}
          {isLoggedIn && ( // show logout button when the user is logged in
            <button className='closeBtn' onClick={handleLogout}>
              Logout
            </button>
          )}
          {!isLoggedIn && ( // show close button when the user is not logged in
            <button className='closeBtn' onClick={onClose}>
              <i class='fa-solid fa-xmark'></i>
            </button>
          )}
        </div>

        {authMode === 'Sign In' ? (
          <>
            <div className='changeAuthFormBtn'>
              <p>Not register yet?</p>
              <button onClick={changeAuthMode}>Sign Up</button>
            </div>
            <form autoComplete='off' onSubmit={handleLogin}>
              <label>
                Name:
                <input type='text' name='name' placeholder='username' />
              </label>
              <label>
                Password:
                <input type='password' placeholder='password' required />
              </label>

              <button className='submitBtn' type='submit'>
                Sign In
              </button>
            </form>
          </>
        ) : (
          <>
            <div className='changeAuthFormBtn'>
              <p>Already registered?</p>
              <button onClick={changeAuthMode}>Sign In</button>
            </div>
            <form autoComplete='off'>
              <label>
                Name:
                <input type='text' name='name' placeholder='username' />
              </label>
              <label>
                Email:
                <input type='email' name='email' placeholder='example@email.com' />
              </label>
              <label>
                Password:
                <input type='password' placeholder='password' required />
              </label>

              <button className='submitBtn' type='submit'>
                Sign Up
              </button>
            </form>
          </>
        )}
      </div>
    </div>
  );
}
