import Banner from "../components/Banner/Banner";
import Info from "../components/InfoSection/info";
// import FeaturedCollections from "../components/FeaturedCollectionSection/FeaturedCollectionSection";
import FeaturedCollections from "../components/FeaturedCollectionSection/FeaturedCollectionSection"
import CategorySection from "../components/CategoriesSection/CategorySection";
import AboutUs from '../components/AboutUs/AboutUs';


import { motion } from 'framer-motion'
import { useAnimation, useInView } from "framer-motion";
import React from "react";

export default function Home({ handleAddToCart }) {

    const AnimatedComponent = ({ children }) => {
        const { ref, inView } = useInView();
        const animation = useAnimation();

        useEffect(() => {
          if (inView) {
            animation.start({
              opacity: 1,
              transition: {
                duration: 1,
              },
            });
          }

          if (!inView) {
            animation.start({ opacity: 0 });
          }
        }, [inView]);

        return <div ref={ref}>{children}</div>;
      };


    return (
        <motion.div initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.6 }}
            exit={{ opacity: 0 }}>
            <Banner />
            <Info />
            <FeaturedCollections handleAddToCart={handleAddToCart} />
            <CategorySection />
            <AboutUs />
        </motion.div>
    )



}
