import "./Sidebar.css"
import { useState } from 'react';
import categories from "./sidebarCategories.json"


export default function CategorySection() {
    const [selectedCategory, setSelectedCategory] = useState('');

    const handleCategoryClick = (category) => {
      setSelectedCategory(category);
    };

    return (
      <section className="categorySection">
        <h2>Categories</h2>
        <div className="categoryContainer">
          <div className='categoryCard'>
            <Link to={{ pathname: '/products', search: `?category=${selectedCategory}` }}>
              <div className='categoryCardImage'>
                <img src={menFashion3} />
              </div>
              <div className='categoryTitle' onClick={() => handleCategoryClick('men')}>Men Fashion</div>
            </Link>
          </div>
        </div>
      </section>
    );
  }
