import './categoriesSection.css'
import hoodieImg from './images/hoodie.jpg';
import tshirtImg from './images/tshirt.jpg';
import menFashion from './images/men_fashion2.jpg'
import menFashion3 from './images/men_fashion3.jpg'
import womanFashion from './images/womanFashion3.jpg'
import jewellery from './images/jewellery2.jpg'
import React, { useEffect, useState } from 'react';

export default function CategorySection() {

    const [products, setProducts] = useState([]); //init it with empty array

    useEffect(() => {

        async function fetchProducts() {
            try {
                const response = await fetch('https://fakestoreapi.com/products');
                const data = await response.json();
                setProducts(data);
                console.log(products)
            } catch (error) {
                console.log("error to fetch the data")

            }
        }
        fetchProducts();
    }, [])

    return (


        <section className="categorySection">
            <h2>Categories</h2>

            <div className="categoryContainer">
                <div className='categoryCard'>
                    <div className='categoryCardImage'>
                        <img src={menFashion3} />
                    </div>
                    <div className='categoryTitle'>Men Fashion</div>
                </div>


                <div className='categoryCard'>
                    <div className='categoryCardImage'>
                        <img src={womanFashion} />
                    </div>
                    <div className='categoryTitle'>Woman Fashion</div>
                </div>

                <div className='categoryCard'>
                    <div className='categoryCardImage'>
                        <img src={jewellery} />
                    </div>
                    <div className='categoryTitle'>Jewellery </div>
                </div>


            </div>

            {/* <button className='showmoreBtn'>Show more</button> */}


        </section>
    )


}
