import { useState, useEffect } from 'react';

import './ProductList.css'
import Sidebar from '../SideBar/Sidebar'
import ProductCard from "./ProductCard";



export default function ProductList({ handleAddToCart, toggleSidebar, showSidebar }) {



    // const [showSidebar, setShowSidebar] = useState(false);

    // const toggleSidebar=()=>{
    //     setShowSidebar(!showSidebar);
    // }


    // fetch data
    const [products, setProducts] = useState([]);

    useEffect(() => {
        async function fetchData() {
            const response = await fetch('https://fakestoreapi.com/products');
            const data = await response.json();
            setProducts(data);
        }

        fetchData();
    }, []);


    // get the categories.
    const [selectedCategories, setSelectedCategories] = useState([]);


    // callback FUNCTION
    const handleCategoryChange = (selectedCategories) => {
        setSelectedCategories(selectedCategories);
        // console.log(selectedCategories)
    };


    // get only the data your want.
    const filteredProducts = products.filter(product => {
        return selectedCategories.includes(product.category);
    });

    return (

        <div className="productListContainer">

            <Sidebar onCategoryChange={handleCategoryChange} toggleSidebar={toggleSidebar} showSidebar={showSidebar} />
            {/* <div className='productListHeader'>
                <h2>Products</h2>
                <button className='filterButton' onClick={toggleSidebar}>Filter</button>
            </div> */}
            <div className="productCardList">

                {filteredProducts.map((product) => (
                    <ProductCard handleAddToCart={handleAddToCart} product={product} key={product.id} />
                ))}
            </div>

        </div>



    )

}
