import React, { useEffect,useRef } from "react"
import './Cart.css'


export default function Cart({ cart, toggleCart, cartVisible, setCart, setCartLength }) {

    const bodyRef = useRef(null);

  useEffect(() => {
    if (bodyRef.current) {
      bodyRef.current.style.overflow = cartVisible ? "hidden" : "auto";
    }
    return () => {
      if (bodyRef.current) {
        bodyRef.current.style.overflow = "auto";
      }
    };
  }, [cartVisible]);

    const handleQuantityChange = (item, change) => {
        const updatedCart = [...cart];
        const updatedItemIndex = updatedCart.findIndex((cartItem) => cartItem.id === item.id);
        const updatedItem = { ...updatedCart[updatedItemIndex] };
        updatedItem.quantity += change;
        console.log("itemQ:", updatedItem.quantity);
        updatedItem.quantity <= 0 ? updatedCart.splice(updatedItemIndex, 1) : updatedCart[updatedItemIndex] = updatedItem;

        setCart(updatedCart);
        setCartLength(updatedCart.reduce((total, item) => total + item.quantity, 0));
    }

    useEffect(() => {
        const body = document.querySelector('body');
        body.style.overflow = cartVisible ? "hidden" : "auto";
        return () => {
            body.style.overflow = "auto";
        };
    }, [cartVisible]);

    const totalCost = cart.reduce((acc, item) => acc + item.price * item.quantity, 0).toFixed(2);


    return (
        <div className={`CartContainer ${cartVisible ? 'visible' : ''}`}>
            <div className="CartContent">
                <div className="closeCartSection">
                    <h3>Cart</h3>
                    <button className="closeCartBtn" onClick={toggleCart}>
                        <i className='fa fa-close'></i>
                    </button>
                </div>
                {
                    cart.map((cartItem) => (
                        <div className="cardItem" key={cartItem.id}>
                            <div className="imageCartContainer">
                                <img src={cartItem.image} alt="cartIcon" />
                            </div>
                            <div className="productCartInfo">
                                <h2 className="cartProductTitle">{cartItem.title}</h2>
                                <div className="cartProductPrice">{cartItem.price * cartItem.quantity} $</div>
                                <div className="quantityCartSection">
                                    <p>
                                        Quantity:
                                        <button onClick={() => handleQuantityChange(cartItem, -1)}>-</button>
                                        <span>{cartItem.quantity}</span>
                                        <button onClick={() => handleQuantityChange(cartItem, 1)}>+</button>
                                    </p>
                                </div>
                            </div>
                        </div>

                    ))
                }
            </div >
            <div className="totalCost">
                <h3>Total Cost: <span>{totalCost} $</span></h3>
                <button>Buy</button>
            </div>

        </div>


    )
}
