import React, { useState, useEffect } from "react"
import "./FeaturedCollectionSection.css"
import productImg from "./images/headset.jpg";

export default function FeaturedCollections() {


    const [products, setProducts] = useState([]); //init it with empty array
    const [sortElement, setSortElement] = useState('all');
    const abortController = new AbortController();

    useEffect(() => {
        async function fetchProducts() {
            let url = 'https://fakestoreapi.com/products?limit=5';

            if (sortElement !== 'all') {
              url = `https://fakestoreapi.com/products?limit=3&sort=desc`;
            }

            try {
              const response = await fetch(url);
              const data = await response.json();
              const filteredData = data.filter((product) => product.category !== 'electronics');
              signal: abortController.signal;

              if (sortElement !== 'all') {
                setProducts([]);
              }

              await setProducts(filteredData);
              console.log();
            } catch (error) {
              console.log("error to fetch the data");
            }
          }          return () => abortController.abort();

          fetchProducts();

    }, [sortElement])

    return (
        <section className="featuredCollections">


            <div className="featuredCollectionsContainer">
                <div className="featuredCollections--Header">
                    <h2>Featured Collections</h2>
                </div>
                <div className="featuredCollections--List">
                    <ul>
                        <li>
                            <button onClick={() => setSortElement("all")}>ALL</button>
                        </li>
                        <li>
                            <button onClick={() => setSortElement("popularity")}>TRENDING</button>
                        </li>
                        <li>
                            <button onClick={() => setSortElement("rating")}>POPULAR</button>
                        </li>
                        <li>
                            <button onClick={() => setSortElement("sold")}>BEST SELLER</button>
                        </li>
                    </ul>
                </div>

                <div className="productCardList">
                    {products.map((product) => (

                        <div className="productCard" key={product.id}>
                            <div className="productImg">
                                <img src={product.image} alt="productImage"/>
                            </div>

                            <div className="productInfo">

                                <div className="productTitle">{product.title}</div>
                                <div className="productPrice">{product.price}$</div>
                            </div>
                            <div className="addToCart"><button>Add to cart</button></div>
                        </div>
                    ))}

                </div>

            </div>







        </section >
    )
}
