import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { Link } from "react-router-dom";
import NavbarItems from '../data/Links.json';
import Modal from "../Modal/Modal";
import logo from '../images/logo3.png';

import "../style.css";
import "./Navbar.css";

export default function Navbar({ cartLength, toggleCart }) {
  const [openModal, setModal] = useState(false);
  const [navbarIcon, setNavbarIcon] = useState("fas fa-bars");
  const [menuOpen, setMenuOpen] = useState(false);
  const [animateCart, setAnimateCart] = useState(false);
  const [localCartLength, setLocalCartLength] = useState(cartLength);

  useEffect(() => {
    if (localCartLength > 0) {
      setAnimateCart(true);
      const timer = setTimeout(() => setAnimateCart(false), 200);
      return () => clearTimeout(timer);
    }
  }, [localCartLength]);

  useEffect(() => {
    setLocalCartLength(cartLength);
  }, [cartLength]);

  function handleNavbar() {
    setNavbarIcon((prevIcon) =>
      prevIcon === "fas fa-bars" ? "fas fa-times" : "fas fa-bars"
    );
    setMenuOpen((prevOpen) => !prevOpen);
  }

  return (
    <nav className="Navbar">
      <div className="NavbarItems">
        <div className="NavbarSiteBrand">
          <Link to="/">
            <img src={logo} alt="logoImg" />
          </Link>
        </div>
        <div className="menu-icon">
          <i className={navbarIcon} onClick={handleNavbar}></i>
        </div>
        <div className="NavbarLinks">
          <ul className={`nav-menu ${menuOpen ? "active" : ""}`}>
            {NavbarItems.map((item, index) => {
              return (
                <li key={index} className={`${item.cName}`}>
                  <Link
                    to={item.url}
                    className="linkItem"
                    onClick={() => {
                      handleNavbar();
                    }}
                  >
                    {item.title}
                  </Link>
                </li>
              );
            })}
            <li className="mobile-actions">
              <button
                aria-label="ProfileActions"
                onClick={() => {
                  setModal(true);
                }}
              >
                Sign Up
              </button>
              <Modal open={openModal} onClose={() => setModal(false)} />
              <button onClick={toggleCart}>
                Cart<span className="cartLength">()</span>
              </button>
            </li>
          </ul>
        </div>
        <div className="NavbarActions">
          <div className="singUpIconSection">
            <button
              aria-label="ProfileActions"
              onClick={() => setModal(true)}
            >
              <i className="fa-solid fa-user"></i>
            </button>
          </div>
          <Modal open={openModal} onClose={() => setModal(false)} />
          <motion.div
            animate={
              animateCart
                ? { scale: [1, 1.2, 1], transition: { duration: 0.2 } }
                : {}
            }
            className="cartIconSection"
          >
            <button onClick={toggleCart}>
              <i className="fa-solid fa-cart-shopping">
                <span className="cartLength">
                  <small>( {localCartLength} )</small>
                </span>
              </i>
            </button>
          </motion.div>
        </div>
      </div>
    </nav>
  );
}
