import Banner from "../components/Banner/Banner";
import Info from "../components/InfoSection/info";
// import FeaturedCollections from "../components/FeaturedCollectionSection/FeaturedCollectionSection";
import FeaturedCollections from "../components/FeaturedCollectionSection/FeaturedCollectionSection"
import CategorySection from "../components/CategoriesSection/CategorySection";
import AboutUs from '../components/AboutUs/AboutUs';

import { motion } from 'framer-motion'

import { useInView } from 'react-intersection-observer';
import { useRef } from 'react';
import { useIntersection } from 'react-use';
import {  AnimatePresence } from 'framer-motion';

export default function Home({ handleAddToCart }) {
    const bannerRef = useRef(null);
    const infoRef = useRef(null);
    const collectionsRef = useRef(null);
    const categoryRef = useRef(null);
    const aboutRef = useRef(null);

    const bannerIntersection = useIntersection(bannerRef, {
      threshold: 0.5,
    });
    const infoIntersection = useIntersection(infoRef, {
      threshold: 0.5,
    });
    const collectionsIntersection = useIntersection(collectionsRef, {
      threshold: 0.5,
    });
    const categoryIntersection = useIntersection(categoryRef, {
      threshold: 0.5,
    });
    const aboutIntersection = useIntersection(aboutRef, {
      threshold: 0.5,
    });

    const handleBannerIntersection = (entry) => {
      if (entry.isIntersecting) {
        console.log('Banner is now visible!');
      }
    };
    const handleInfoIntersection = (entry) => {
      if (entry.isIntersecting) {
        console.log('Info is now visible!');
      }
    };
    const handleCollectionsIntersection = (entry) => {
      if (entry.isIntersecting) {
        console.log('Collections is now visible!');
      }
    };
    const handleCategoryIntersection = (entry) => {
      if (entry.isIntersecting) {
        console.log('Category is now visible!');
      }
    };
    const handleAboutIntersection = (entry) => {
      if (entry.isIntersecting) {
        console.log('About is now visible!');
      }
    };

    const fadeInAnimation = {
      opacity: 1,
      transition: { duration: 0.6, ease: 'easeInOut' },
    };

    return (
      <motion.div>
        <div ref={bannerRef}>
          <Banner />
        </div>
        <AnimatePresence>
          {bannerIntersection && bannerIntersection.isIntersecting && (
            <motion.div
              ref={infoRef}
              initial={{ opacity: 0 }}
              animate={fadeInAnimation}
              exit={{ opacity: 0 }}
            >
              <Info />
            </motion.div>
          )}
        </AnimatePresence>
        <div ref={collectionsRef}>
          <FeaturedCollections handleAddToCart={handleAddToCart} />
        </div>
        <AnimatePresence>
          {categoryIntersection && categoryIntersection.isIntersecting && (
            <motion.div
              ref={categoryRef}
              initial={{ opacity: 0 }}
              animate={fadeInAnimation}
              exit={{ opacity: 0 }}
            >
              <CategorySection />
            </motion.div>
          )}
        </AnimatePresence>
        <div ref={aboutRef}>
          <AboutUs />
        </div>
      </motion.div>
    );
  }
